<script>
	import RegisterPopup from "../popup/RegisterPopup";
  import LoginPopup from "../popup/LoginPopup";
  import UseaffPopup from "../popup/UseaffPopup";
  import BalancePopup from "../popup/BalancePopup";
  import ProfilePopup from "../popup/ProfilePopup";
  import ResetpassPopup from "../popup/ResetpassPopup";
  import WelcomePopup from "../popup/WelcomePopup";
  import WonNftsPopup from "../popup/WonNftsPopup";
  import RainPopup from "../popup/RainPopup";
  import ChatrulesPopup from "../popup/ChatrulesPopup";
  import SetwalletsPopup from "../popup/SetwalletsPopup";
  import SetseedPopup from "../popup/SetseedPopup";
  import ClaimunamePopup from "../popup/ClaimunamePopup";
  import ExchangeCrPopup from "../popup/ExchangeCrPopup";


  export default {
    components: {
      WelcomePopup,ResetpassPopup,ProfilePopup,RegisterPopup,LoginPopup,UseaffPopup,BalancePopup,WonNftsPopup,RainPopup,ChatrulesPopup,SetwalletsPopup,SetseedPopup,ClaimunamePopup,ExchangeCrPopup},
    mounted() {
      this.$nextTick(() => {
        console.log("LOADED");
        this.rippleEffect();
        this.onwoninvClick();
        setTimeout(() => {
          console.log("LOADED2");
          //$("#siteLoader").addClass("hideloader");
          let loaders = document.getElementById('siteLoader');
          loaders.classList.add('opacityhideloader');
          loaders.addEventListener('transitionend', function(e) {
            loaders.classList.add('hideloader');
          });
        }, 800);
      });
      window.onload = function() {
        console.log("allready");
      };
    },
    methods: {
      rippleEffect(){
        $('#app').on('click', '.js-ripple', function(e) {
          var $this = $(this);
          var $offset = $this.parent().offset();
          var $circle = $this.find('.c-ripple__circle');

          var x = e.pageX - $offset.left;
          var y = e.pageY - $offset.top;

          $circle.css({
            top: y + 'px',
            left: x + 'px'
          });

          $this.addClass('is-active');
          setTimeout(function() {
            $this.removeClass('is-active');
          }, 400);
        });
      },
      onwoninvClick(){
        $('#app').on('click', '.nftdroppednx', function(e) {
          let idnftdi = $(this).data('id');
          console.log("clicked:"+idnftdi);
          $('.nftdroppednx').removeClass("activenftinsn");
          if(!$(this).hasClass("activenftinsn")){
            $(this).addClass("activenftinsn");
          }
        });
      }
    },
    computed: {
			userData() {
				return this.$store.getters.userData;
			},
			hasUserData() {
				return this.$store.getters.hasUserData;
			}
		}
	}
</script>
